// Author: blackpolygon
// Title:  Galaxy

// Based on 'Audio Eclipse' by airtight
// https://www.shadertoy.com/view/MdsXWM


const float dots = 300.; 
float radius = 0.0050;
const float brightness = 0.00025;
const float TWOPI = 6.28318;

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st=(fragCoord.xy-.5*iResolution.xy)/min(iResolution.x,iResolution.y);
    vec3 bgColor = vec3(0.0); 

    st = rotate2d( sin(iTime/5.)*3.14 ) * st;

    for(float i=0.;i<dots; i++){

        radius += (0.5+sin(iTime/3.)*0.3)*0.002;

        //Values going between 1 and 0
        float b1a0 = 0.5+sin(iTime)*0.5;
        float b1a02 = 0.5+cos(iTime)*0.5;

		//get location of dot
        float x = radius*cos(TWOPI*float(i)/(dots/(15.+b1a0)));
        float y = radius*sin(TWOPI*float(i)/(dots/(14. +b1a02)));
        vec2 position = vec2(x,y);

        //get brightness of this pixel based on distance to dot
		bgColor += brightness/(length(st-position));
    }

	fragColor = vec4(bgColor,1);
}
