// Author: blackpolygon
// Title: Melting lines

//Based on 'Wood' by patriciogv


float random (in vec2 st) { 
    float cs = iTime*0.0005;
    return fract(sin(dot(st.xy,
                         vec2(0.1+cs,0.820))) 
                * 88.049);
}

// Value noise by Inigo Quilez - iq/2013
// https://www.shadertoy.com/view/lsf3WH
float noise(vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);
    vec2 u = f*f*(3.0-2.0*f);
    return mix( mix( random( i + vec2(0,0) ), 
                     random( i + vec2(1.0,0.0) ), u.x),
                mix( random( i + vec2(0.0,1.0) ), 
                     random( i + vec2(1.0,1.0) ), u.x), u.y);
}

mat2 rotate2d(float angle){
    return mat2(cos(angle),-sin(angle),
                sin(angle),cos(angle));
}

float lines(in vec2 pos, float b){
    float b1a0 = 0.5 + sin(iTime)*0.5;
    float b1a1 = sin(iTime);
    float scale = 3.;
    pos *= scale;
    return smoothstep(0.0,
                    0.500+b*.5,
                    abs((sin(pos.x*4.189)+b*b1a1))*0.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/min(iResolution.x,iResolution.y);
    st.x -= 0.35;

    st *= iResolution.y/iResolution.x;
    st -= 0.5;
    st = rotate2d( sin(iTime*0.15)*3. ) * st;
	float cs = 5.+ sin(iTime)*0.2*(iTime*0.05);
    vec2 pos = st.yx*vec2(2.360+cs, 1.208+cs);

    float pattern = pos.x;

    pos = rotate2d( noise(pos) ) * pos;

    pattern = lines(pos,0.5);
    vec3 color = vec3(0.985, 0.3+(0.5+cos(iTime)*0.5),0.521) * vec3(pattern);

    fragColor = vec4(color, 1.);
}
