bool isInTriangle(in vec2 fragCoord, in vec2 centerPosition, in float sideLength, in float rotation)
{
    float s = sin(rotation);
  	float c = cos(rotation);

  	// translate point back to origin:
  	vec2 point = fragCoord - centerPosition;

  	// rotate point
  	float xnew = point.x * c - point.y * s;
  	float ynew = point.x * s + point.y * c;

  	// translate point back:
  	fragCoord = vec2(xnew + centerPosition.x, ynew + centerPosition.y);

	// equations for the 3 lines

    // is it below the bottom line
    if (fragCoord.y < centerPosition.y - sideLength)
    {
     	return false;   
    }

    // is it outside the left line
    if (fragCoord.y + centerPosition.y * 2.0 > (1.73 * fragCoord.x + sideLength))
    {
    	return false;    
    }

    // is it outside the right line
    if (fragCoord.y  + centerPosition.y * 2.0 > (-1.73 * fragCoord.x) + centerPosition.x * 2.0 * 1.73 + sideLength)
    {
     	return false;   
    }

    return true;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    if (isInTriangle(fragCoord, vec2(iResolution.xy/2.0), 60.0 + (30.0 * sin(iTime * 2.0)), 3.1415 * sin(iTime)))
    {
		fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
    }
}
