void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = mod(iTime, 1.0) + fragCoord.xy / iResolution.xy;
	fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
    fragColor += texture(iChannel0, uv);
    fragColor -= 0.75;

    //Move our coordinate space from (0..1) on both axes to (-1..1) on both axes, with (0,0) in the center
    uv = uv * 2.0 - 1.0;

    //Since the coordinate space is now (-1, 1), the maximum length possible is sqrt(1 + 1) i.e. sqrt(2).
    //We'll normalize the length by this.
    float len = length(uv) / sqrt(2.0);

    //You can try visualizing len by outputing fragColor = vec4(len)

    //Using the length as our key into the texture
    vec2 firstRow = vec2(len, 0.25);

    fragColor /= texture(iChannel1, firstRow);

    //Dim the output so that it doesn't look too saturated
    fragColor -= mod(iTime, 50.0) / 100.0;
    fragColor -= 0.8;
}
