float wave_mag(in vec2 uv, in float vert_offset, in float amp, in float base_p, in float pshift_shift, in float pshift_mult)
{
    float phase_shifter = iTime;
    //float pshift_mult = 2.0;
    float period = base_p; //sin(iTime * 0.75) * 10.0 + base_p;
    float mag = 1.0 - distance(uv.y, vert_offset + amp * sin(uv.x * period + (phase_shifter + pshift_shift) * pshift_mult));
    mag = pow(mag, 200.0);
    return mag;
}

vec4 wave_group_one(in vec2 uv)
{
    float mag  = wave_mag(uv, 0.5, 0.25, 8.0, 0.0, 2.0);
    float mag2 = wave_mag(uv, 0.5, 0.25, 8.0, 4.7, 2.0);
    float mag3 = wave_mag(uv, 0.5, 0.11, 8.0, 0.0, 2.0);
    float mag4 = wave_mag(uv, 0.5, 0.11, 8.0, 4.7, 2.0);
    float mag5 = wave_mag(uv, 0.5, 0.09, 8.0, 0.0, 2.0);
    float mag6 = wave_mag(uv, 0.5, 0.09, 8.0, 4.7, 2.0);

    vec4 color = vec4(0.0, 0.0, 0.0, 0.0);
    color += vec4(0.0, mag, mag, 1.0);
    color += vec4(mag2, 0.0, mag2, 1.0);
    color += vec4(mag3, mag3, 0.0, 1.0);
    color += vec4(mag4, mag4, mag4, 1.0);
    color += vec4(mag5, mag5, 0.0, 1.0);
    color += vec4(mag6, mag6, mag6, 1.0);

    return color;
}

