void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor.r = texture(iChannel0, vec2(uv.x - 0.005 * sin(iTime * 3.), uv.y)).r;
	fragColor.g = texture(iChannel0, vec2(uv.x, uv.y)).g;
	fragColor.b = texture(iChannel0, vec2(uv.x + 0.005 * sin(iTime * 3.), uv.y)).b;
	vec2 screenSpace = uv * iResolution.xy;
    fragColor.rgb -= sin((screenSpace.y + (iTime * 29.0))) * 0.02;
}
