void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;

    uv *= 3.0;
    vec2 center = 3.0 * vec2(.85 + .15 * sin(iTime / 2.5), .5 + .15 * cos(iTime / 2.5));


    if (sin(iTime / 2.5) > 0.0) {
        //uv.y = 1.0 - uv.y;
    }

    vec4 texColor = texture(iChannel0,uv);//Get the pixel at xy from iChannel0

    // Texture 1 setup
    float avg1 = (texColor.x + texColor.y + texColor.z) / 3.0;
    float weight1 = abs(sin(iTime / 2.5));
    //float weight2 = 1.0 - pow(abs(sin(iTime / 2.5)), 2);
    float weight2 = 1.0 - pow(sin(iTime / 2.5), 4.0);

    texColor = vec4((avg1 * weight1) + (texColor.x * (1.0 - weight1)), 
     (avg1 * weight1) + (texColor.y * (1.0 - weight1)),
     (avg1 * weight1) + (texColor.z * (1.0 - weight1)), texColor.w);

    float dist =  .75 * abs(sin(iTime / 2.5));
    fragColor = texColor;

    // Texture 2 setup
    vec4 texColor2 = texture(iChannel1, uv);
    float avg2 = (texColor2.x + texColor2.y + texColor2.z) / 3.0;

    texColor2 = vec4((avg2 * weight2) + (texColor2.x * (1.0 - weight2)), 
     (avg2 * weight2) + (texColor2.y * (1.0 - weight2)),
     (avg2 * weight2) + (texColor2.z * (1.0 - weight2)), texColor2.w);


    // Texture 3 setup
    vec4 texColor3 = texture(iChannel2, uv);
    float avg3 = (texColor3.x + texColor3.y + texColor3.z) / 3.0;

    texColor3 = vec4((avg3 * weight2) + (texColor3.x * (1.0 - weight2)), 
     (avg3 * weight2) + (texColor3.y * (1.0 - weight2)),
     (avg3 * weight2) + (texColor3.z * (1.0 - weight2)), texColor3.w);

    // Texture 4 setup
    vec4 texColor4 = texture(iChannel3, uv);
    float avg4 = (texColor4.x + texColor4.y + texColor4.z) / 3.0;

    texColor4 = vec4((avg4 * weight2) + (texColor4.x * (1.0 - weight2)), 
     (avg4 * weight2) + (texColor4.y * (1.0 - weight2)),
     (avg4 * weight2) + (texColor4.z * (1.0 - weight2)), texColor4.w);

    if (fract(distance(uv, center)) <= dist)	{
        if (cos(iTime / 3.75) > -0.5 && sin(iTime / 3.75) > 0.0) {
           fragColor = texColor2;
        }
        else if (cos(iTime / 3.75) > -0.5 && sin(iTime / 3.75) < 0.0 ) {
           fragColor = texColor3;
        }
        else {
           fragColor = texColor4;
        }
    }

}
