float r( vec2 s) {return fract(9993.*sin(s.x*9.+s.y*7.));}
void mainImage(out vec4 o, vec2 i){
    vec2 j = fract(i*=99./iResolution.x), 
         p = i-j+ vec2(3,floor(iTime*50.*fract(sin(i-j).x)));
    i = abs(j-.2);
    o =  vec4(r(ceil(p*9.+5.*j))>.5&&i.x<.3&&i.y<.100 ?   1. - r(p)*(4.-dot(i+1.,i)*12.)  :  2.);
}
