//Weierstrass function
float W (float g,float i)
{
return cos(g*pow(3.,i))/pow(2.,i+1.) ;
}

void mainImage( out vec4 I, in vec2 fragCoord )
{
    vec2 C = ((fragCoord/iResolution.xy)*2.-vec2(.5,1.))*vec2(6.2831853,1.);
    int L = int(mix(1.,7.,fract(iTime*.2)));      
    float F = C.y - W(C.x,0.);

for(int i = 0; i < L-1; i++)
{
	F -= W(C.x,float(i+1));
} 

	F = 1.-ceil(clamp(F,0.,1.));

    // Output to screen
    I = vec4(F,F,F,1.);
}
