precision highp float;
#define SS 2

#define PI 3.14159265359
#define TAU (PI*2.)

#define time (iTime)

vec3 subsample(vec2 fc){
    vec2 res= floor(iResolution.xy);
    vec2 uv = fc/res;
    vec2 s= uv*2.-1.;
    s.x*= res.x/res.y;
    uv= s*4.;

    float rp= length(uv);
    float t= (atan(uv.y,uv.x))+TAU/12.;

    float f=1.;
    float v= (sin(time*8.)*.5+.5);
    v= exp2(v*3.-.5);
    float t2= mix(1.,6.,sin(time*1.)*.5+.5);
    int iters= 1+int(v);
    for(int i=0;i!=iters; i++){
        float c= pow(t2,float(i));
    	f+= cos(t*c*3.)/pow(2.,float(i));
    }

    float m= length(vec3(0.,(f*normalize(uv)-uv)));
    m= pow(m,(.5));
    vec3 cl= vec3(m);
    return cl;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 fc = floor(fragCoord);

    vec3 c= vec3(0);
    for(int y=0; y!=SS; y++){
    	for(int x=0; x!=SS; x++){
            c+= subsample(fc + vec2(x,y)/float(SS));
        }
    }
    c/=float(SS*SS);
    c= pow(c,vec3(.7));

    fragColor= vec4(c,1);
}
