void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;
	float grid = 3.;
    // Time varying pixel color
    vec2 distortedUV = uv*grid+0.2*vec2(noise(uv*5.),noise(uv*2.+8.+vec2(0.,iTime*2.)));

    // Output to screen
    vec4 eyes = googs(distortedUV,iTime);
    distortedUV = uv*grid+0.95*vec2(noise(uv*5.),noise(uv*4.+8.+vec2(0.,iTime)));
    fragColor = vec4(mix(blots(distortedUV,-iTime*5.).rgb,eyes.rgb,eyes.a),1.);
}

vec2 random2(vec2 st){
    st = vec2( dot(st,vec2(127.1,311.7)),
              dot(st,vec2(269.5,183.3)) );
    return -1.0 + 2.0*fract(sin(st)*43758.5453123);
}

// Value Noise by Inigo Quilez - iq/2013
// https://www.shadertoy.com/view/lsf3WH
float noise(vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    vec2 u = smoothstep(0.,1.,f);

    return mix( mix( dot( random2(i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ),
                     dot( random2(i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( random2(i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ),
                     dot( random2(i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
}

vec4 blots(vec2 st,float offset) {
    vec2 sn_st = fract(st)*2.-1.;
    float rota = dot(floor(st),vec2(1.,1.))*0.5+offset;
    float blob = smoothstep(1.8,0.,length(sn_st));
    float shape = blob + (sin(atan(sn_st.x,sn_st.y)*4.+rota)+0.2)*0.15;
    shape = smoothstep(0.8,0.85,shape);
    float crossy = shape;
    shape = sin(atan(sn_st.x,sn_st.y-0.05)+1.62)*0.4+blob*0.65;
    shape = smoothstep(0.8,0.85,shape);

    vec3 color1 = vec3(0.7,0.9,0.6)*shape;
    vec3 color2 = mix(vec3(0.9,0.4,0.2),vec3(0.2,0.3,0.35),step(sn_st.y,0.))*crossy;

    return vec4(mix(color2,color1,shape),1.);
}

vec4 googs(vec2 st,float offset){
	vec2 sn_st = fract(st)*2.-1.;
    float rota = dot(floor(st),vec2(1.,1.))*0.5+offset;
    float blob = smoothstep(1.8,0.,length(sn_st));
   	float eyeball = smoothstep(0.85,0.86,blob);
    float goog = smoothstep(0.18,0.2,length(sn_st-vec2(sin(rota),cos(rota))*0.22));

    vec4 mixed = vec4(vec3(eyeball*goog),eyeball);
    return mixed;
}
