float random (in vec2 v) {
    return fract(sin(dot(v.xy,vec2(12.9898,78.233)))*43758.5453123);
}

// Based on Morgan McGuire @morgan3d
// https://www.shadertoy.com/view/4dS3Wd
float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    vec2 u = f * f;

    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

float pi=3.141592653;
float gua_d(float x, float miu, float fi) {
	return 1./(fi*sqrt(2.*pi))*exp(-(x-miu)*(x-miu)/(2.*fi*fi));
}

float r = 50.;
float t;
float d;
float m;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
	d = length(fragCoord - iMouse.xy);
    t = 0.1 * iTime;

    float n = noise(uv * 3.45 + iTime);
    d = length(fragCoord - iMouse.xy) + 100. * (n - .5);
    m = mix(1., 0., d/40.);
    float c1 = m;

    n = noise(uv * 13.45 + iTime);
    d = length(fragCoord - iMouse.xy) + 30. * (n - .5);
	m = gua_d(d, r, .5);
    m += 4. * gua_d(d, r, 15.);	
    m += fract(t) * gua_d(d, r, .3);
	float c2 = mix(0.,1.,m);

    n = noise(uv * 27.45 + iTime);
    d = length(fragCoord - iMouse.xy) + 70. * (n - .5);
	m = gua_d(d, r, .5);
    m += 7. * gua_d(d, r, 25.);	
    m += fract(t) * gua_d(d, r, .3);
    float c3 = mix(0.,1.,m);

    fragColor = vec4(vec3(c1 + c2 + c3),1.0);
}
