vec2 rotate2D(vec2 uv, float angle)
{
    uv -= sin(iTime*0.7);
    uv = mat2(cos(angle),-sin(angle),sin(angle),cos(angle))*uv;
    uv += cos(iTime*0.8);
    return uv;
}
#define TAU 6.2831

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float rotation = TAU * 0.125; //sin(iTime);// 0.125;
    vec2 size = vec2(5.0,5.0);
   	uv = rotate2D(uv,rotation);

    float total = floor(uv.x*size.x) + floor(uv.y*size.y);
    bool isEven = mod(total,2.0) == 0.0f;

    vec4 color1 = vec4(0.0,0.0,0.0,1.0);
    vec4 color2 = vec4(1.0,1.0,1.0,1.0);

    // Output to screen
    fragColor = isEven ? color1:color2;
}
