void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    uv -= 0.5; // position du centre

    float angle = atan(uv.y, uv.x); //-pi et pi
    float radius = length(uv);


    angle /= 0.5;
    radius *= 5.;


    float osc = sin(iTime)*0.5+20.;

    uv = vec2(angle+osc, radius * 10.);

    uv.x += angle*30.; 
    uv.y -= iTime;

    vec4 col = texture (iChannel0, uv);

    float lum = (col.x + col.y + col.z)/3.0;

    vec4 colA = vec4(0.0, 0.0, 0.5, 1.0);
    vec4 colB = vec4(0.0, 0.0, 0.5 , 1.0);

    col = mix(colA, colB, osc);
    col += lum;


	fragColor = col;
}
