#define white vec4(1, 1, 1, 1)
#define black vec4(0, 0, 0, 0)

float dist(vec2 c, vec2 v) {
    vec2 d = c - v;
	return sqrt(d.x * d.x + d.y * d.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 center = vec2(iResolution.x / 2., iResolution.y / 2.);

    float speed = 3.;
    float baseRadius = 350.;
    float range = 25.;

	float radius = baseRadius + range * sin(iTime * speed);
    float x = fragCoord.x - center.x;
    float y = fragCoord.y - center.y;
    if(x * x + y * y > radius * radius) fragColor = black;
    else fragColor = white - white * (dist(center, fragCoord) / radius);
}
