float Width = 7.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
 vec2 uv = fragCoord.xy / iResolution.xy;

    //colors
 vec3 blue = vec3(1.0,0.0,0.0);
 vec3 red = vec3(0.0,0.0,1.0);

 float aspectRatio = iResolution.y/iResolution.x;

    // makes a square
 float horz = floor( uv.x * Width);
 float vert = floor(uv.y * Width * aspectRatio);
 float total = (horz + vert);

    //checks to see what color a pixel should be assigned based on its location
 if( mod(total, 2.0) == 0.0)
 {
 	fragColor = vec4(blue,1.0);
 }

 else
 {
 	fragColor = vec4(red,1.0);
 }

}
