renderpass.code:
vec2 translate(vec2 p, vec2 o)
{
    return p - o;
}

float SDbox(vec2 uv, vec2 r)
{
    return length(max(abs(uv)-r,vec2(.07)));
}

float SDcircle(vec2 uv, float r)
{
    return length(uv)-r;
}

float SDunion(float a, float b)
{
    return min(a,b);
}

float R2D(vec2 uv)
{
    return fract(sin(dot(uv,vec2(120.,670.)))*4563.);
}

float noise(vec2 uv, float zoom)
{
    vec2 i_uv = floor(uv*zoom);
    vec2 f_uv = fract(uv*zoom);

    float a = R2D(i_uv);
    float b = R2D(i_uv+vec2(1.0,0.0));
    float c = R2D(i_uv+vec2(0.0,1.0));
    float d = R2D(i_uv+vec2(1.0,1.0));

    vec2 u = smoothstep(.0,1.,f_uv);

    float ab = mix(a,b,f_uv.x);
    float cd = mix(c,d,f_uv.x);

    return mix(ab,cd,f_uv.y);

}

float noise2(vec2 st)
{
    float n = noise(st,8.)*.5;
    n += noise(st,16.)*.25;
    n += noise(st,32.)*.125;
    n += noise(st,64.)*.0625;

    return n;
}

vec3 c1 = vec3(.85,.12,.0);
vec3 c2 = vec3(.98,.54,.01);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy*2.-1.;
    uv.x *= iResolution.x/iResolution.y;

    vec2 st = vec2(uv);
    st.y -= iTime*.05;

    float n = noise2(st);
    float v = abs(sin(iTime*.5))*n;

    vec2 p1 = translate(uv,vec2(.0,-.3));
    float b1 = SDbox(p1,vec2(6.,.001));
    b1 = smoothstep(.1,.16,b1);
    b1 += smoothstep(.1,.2,b1*n);

    vec2 p2 = translate(uv,vec2(-.5,.0));
    float b2 = SDbox(p2,vec2(.05,6.));
    b2 = smoothstep(.1,.16,b2);
    b2 += smoothstep(.1,.9,b2*n);

    float b = SDunion(b1,b2);

    float c = SDcircle(uv,.6);
    c = smoothstep(.1,.5,c);
    c += smoothstep(.1,.2,c*n);

    float t = b-c;

    vec3 col = vec3(0.);
    col = (mix(c1,c2,(abs(sin(iTime*.5))+.5)*(n*2.))+n)*t;

    // Output to screen
    fragColor = vec4(col,1.0);
}
