void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;
    vec2 uv = fragCoord / iResolution.xy;
    float brickNumber = 5.0;

    vec4 c1 = vec4(0.8, 0.9, 0.0, 1.0);
    vec4 c2 = vec4(0.0, 0.7, 1.0, 1.0);

	uv *= brickNumber;

    if(fract(uv.y - 0.5) > 0.5) {
    	uv.x += 0.5;
    }

    uv.y *= 2.0;
    uv.y += 0.5;

    vec2 p = fract(uv);
    vec2 b = step(0.02, abs(p - 0.5));
    //float m = b.x * b.y;
    float m = min(b.x, b.y);

    fragColor = mix(c2, c1, m);
}
