void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 col = texture(iChannel0, vec2(uv.x, uv.y));
    fragColor = (vec4(cos(uv.y))/col)*vec4(0.9, 0.5, 0.1, 1.0);
    if(fragColor.r > 0.0000000000001 && fragColor.r < 0.3)
    {
        fragColor = vec4(0.9, 0.5, 0.1, 1.0);
    }
    if(fragColor.rgb == vec3(0.9, 0.5, 0.1))
    {
        fragColor = texture(iChannel1, uv);
    }
}

