renderpass.code:
#ifdef GL_ES
precision highp float;
#endif


float snoise(vec3 v)
{
    return length(texture(iChannel0, v.xy * 0.1));
}

#define STARDISTANCE 1000.
#define STARBRIGHTNESS 0.85
#define STARDENCITY 0.01


float hash13(vec3 p3)
{
	p3  = fract(p3 * vec3(.1031,.11369,.13787));
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

float stars(vec3 ray, float density, float distance)
{
    vec3 p = ray * distance / iResolution.x * 1024.0;
    float brigtness = smoothstep(1.0 - density, 1.0, hash13(floor(p)));
    return clamp(smoothstep(STARBRIGHTNESS, 0., length(fract(p) - 0.5)) * brigtness, 0.0, 1.0);
}



float fbm(vec3 x) {

    x = normalize(x) * 0.5;
    float u = 0.5 + atan(x.z, x.x) / 2.0 / 3.1415;
    float v = 0.5 - asin(x.y) / 3.1415;
    return length(texture(iChannel1, vec2(u,v)));
}

vec3 fg_Position;

float stars() {

	float iSize = max(iResolution.x, iResolution.y);
    return stars(normalize(fg_Position.xyz / iSize), STARDENCITY, STARDISTANCE);
}

float BackgroundBrightness = 1.0;

void mainImage( out vec4 o, in vec2 f )
{
    vec2 m = iMouse.xy / iResolution.xy;
    if (length(iMouse.xy) == 0.0) {
        m = vec2(0.5, 0.6);
    }
    f = (f*2.0 - 1.0 ) / iResolution.xy;
    f.y -= (m.y - 0.5)* 8.0;
    f.x -= (m.x - 0.5)* 8.0;
    fg_Position = vec3(f, abs(1.0 - f.y * f.y - f.x * f.x));
	vec3 pos = normalize(fg_Position.xyz);


    float y = 1.0 - abs(pos.y + snoise(pos * 10.0) * 0.005);
    y = y*y;

    float core = pow(abs(pos.x), 8.0) * pow(y, 4.0);
    if (pos.x < 0.0) core *= 0.15;
    else core *= 0.5;

    float galaxy = core + y;

    float glow = max((galaxy - pow(galaxy, 6.0)) * galaxy, 0.0) * 2.25;

    float cloud = clamp(fbm(pos * 5.0) * fbm(pos * 20.0) * galaxy * pow(y, 4.0) * 2.0 * (1.0 - core * 0.85), -1.0, 1.0);

    vec3 s = vec3(stars() * (1.0 - cloud)) * mix(vec3(1.0,1.0,1.0), vec3(1.0,0.39,0.0), pow(snoise(pos * 100.0), 0.5))
    * abs(snoise(pos * 100.0));
    s += vec3(stars() * (1.0 - cloud)) * vec3(1.0,1.0,1.0)
    * abs(snoise(pos * 100.0 + 5.0) * 2.0);

    // large clouds
    o.rgb += vec3(0.6,0.47,0.84) * fbm(pos * 10.0) * pow(galaxy * 1.0, 2.0);
    o.rgb += vec3(0.73,0.59,0.87) * fbm(pos * 10.0) * pow(y * galaxy * 1.0, 4.0) * (1.0 / pow(BackgroundBrightness, 0.25) * 1.2);

    // bright clouds
    o.rgb += max(max(vec3(1.0,0.95,0.05) * fbm(pos * 10.0) * y * pow(galaxy, 4.0),
    vec3(1.0,0.9,0.4) * fbm(pos * 10.0) * y * y * pow(galaxy, 4.0)),
    vec3(1.0,0.9,0.4) * fbm(pos * 15.0) * y * y * pow(galaxy, 4.0));


    float galaxyStarBrightness = 2.0;

    // galaxy stars
    s += vec3(stars(pos * 2.0, min(STARDENCITY * 2.0, pow(galaxy * 1.0, 4.0) * 0.5), 500.0)) * (1.0 - cloud) * galaxyStarBrightness;
    s += vec3(stars(pos * 2.1, min(STARDENCITY * 4.0, pow(galaxy * 1.0, 8.0) * 0.5), 500.0)) * (1.0 - cloud) * galaxyStarBrightness;
    s += vec3(stars(pos * 2.2, min(STARDENCITY * 5.0, pow(galaxy * 1.0, 16.0) * 0.5), 500.0)) * (1.0 - cloud) * galaxyStarBrightness;


    o.rgb += vec3(0.15,0.19,0.27) * (core * 0.75  + 0.25 * core * fbm(o.rgb * 0.0001));

    o.rgb += max(0.0, log(y * 1.5))* vec3(0.9,0.85,0.36);
    o.rgb += pow(y * (1.0 - core), 8.0) * 0.65;


    o.rgb += max(min(1.0 - pow(1.0 - core, 4.0), 1.0 - core), 0.0) * pow((1.0 - core), 4.0);

    // core glow
    o.rgb = mix(o.rgb, vec3(0.0,0.0,0.0), -pow(core * pow(y, 0.5), 1.0));


    // dust clouds
    o.rgb = mix(o.rgb, vec3(0.24,0.16,0.15), cloud / 1.75);


    o.rgb = o.rgb * pow(BackgroundBrightness, 2.0);
    o.rgb = clamp(o.rgb, 0.0, 1.0) * pow(BackgroundBrightness, 0.25);
    o.rgb = s * pow(BackgroundBrightness, 0.5) + o.rgb;


    o.a = 1.0;
}
