void mainImage( out vec4 fragColor, in vec2 fragCoord ){
 	 vec2 p = (2.0*fragCoord.xy-iResolution.xy)/iResolution.y;

 	 float tau = 3.1415926535*2.0;
     float a = atan(p.x,p.y)/tau - iTime/10.;
     float r = length(p)*0.75;

     a = mod(a , .333);
     a *= 1.3;

     r *= r*a*9.;

     vec2 uv = -1.0 + 2.0 * fragCoord.xy / iResolution.y;
 	 uv.x -= 0.77;

     vec3 bcol = vec3(0.2,0.2,0.3-0.07*uv.y)*(1.0-0.55*length(uv)) * 0.4;		
     vec3 hex = vec3(0.,0.,0.5*hexf(uv*1.5));	 
     vec3 col = mix(bcol, hex, (length(hex)));

     if(r > 0.25 && r <1.3 * a){
     	fragColor = vec4(a ,r, 0.8 , 1.0);
     }
     else{
     	fragColor = vec4(col,1.0);
    }
 }
