float packRGB(vec3 v)
{
	float x = v.r*(255.0/256.0);
    x = floor(x * 256.0) + v.g*(255.0/256.0);
    x = floor(x * 256.0) + v.b*(255.0/256.0);
    return x;
}

vec3 unpackRGB(float f)
{
    vec3 re;
    re.b = fract(f);
    f /= 256.0;
    re.g = fract(f);
    f /= 256.0;
    re.r = f;
    return re;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 og = texture(iChannel0, uv).rgb;
	//vec3 og = vec3(uv.x, 1.0, uv.x);
   	float chan = packRGB(og);

    vec3 re = unpackRGB(chan);

	bool showDiff = iMouse.x > iResolution.x * 0.5;
    bool showOg = iMouse.y > iResolution.y * 0.5;
    vec3 cc;

    if (showDiff)
    	fragColor = vec4(abs(re - og) * 100.0, 1.0), cc = vec3(1.0, 0.0, 0.0);
    else if (showOg)
        fragColor = vec4(og, 1.0), cc = vec3(0.0, 1.0, 0.0);
	else
		fragColor = vec4(re, 1.0), cc = vec3(0.0, 0.0, 1.0);

    if (uv.x < 0.1 && uv.y < 0.1)
		fragColor = vec4(cc, 1.0);
}
