void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord.xy / iResolution.xy;

    // distance float
    float d = distance(vec2(iResolution.x / 20., iResolution.y / 100.2), fragCoord.yy) * (1./(iResolution.x*.006))*(0.013+(.002*sin(iTime)));

    // colors
    vec3 sand = vec3(231./255., 200./255., 173./255.);
    vec3 sandBlue = vec3(208./255., 198./255., 192./255.);
    vec3 lightBlue = vec3(103./255., 120./255., 190./255.);
    vec3 blue = vec3(24./255., 46./255., 77./255.);

    vec3 color = mix(
        mix(sand, sandBlue, uv.x),
        mix(lightBlue, blue, uv.y),
       	d
    );	


    fragColor = vec4(color, 1.);
}
