vec2 even(vec2 x) {
    return (mod(x, 2.0) - 0.5) * 2.0;
}

vec2 movingLayers(vec2 _st, float _layers, float _speed){
    float time = iTime*_speed;

    vec2 grid = floor(fract(_st) * _layers);    
    vec2 splitter = fract(time) > 0.5 ? vec2(1.0, 0.0) : vec2(0.0, 1.0);

	_st += even(grid.yx) * splitter * time * 2.0;
    return fract(_st);
}

float circle(vec2 _st, float _radius){
    vec2 pos = 0.5 - _st;
    return smoothstep(_radius,_radius+0.01,length(pos));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 st = fragCoord/iResolution.y;
    st = movingLayers(st*2.,9.,0.15);
    fragColor = vec4(vec3(circle(st, .3)), 1.);
}
