vec4 COLOR_1 = vec4(0.921, 0.474, 0.141, 1.);
vec4 COLOR_2 = vec4(0.411, 0.466, 0.847, 1.);
vec4 COLOR_3 = vec4(0.929, 0.929, 0.870, 1.);

uniform float u_time;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float n = smoothstep(0., 0.5, uv.y);

    float q = n * (1. - uv.x);

	vec4 o1 = mix(COLOR_1, COLOR_2, 1. - uv.x);
    vec4 o2 = mix(o1, o1 * n, 0.5 * (1.-(sin(iTime) * 0.5) + 0.5) /1.6 + 0.2);
    float o3 = smoothstep(0., 0.5, 1.0-uv.x - 0.3) * 1.0 - smoothstep(1., 0.5, uv.y);

    float wt = mix(0.1, 0.5, sin(iTime / uv.x*5. / uv.y * 5.) *atan(222.*iTime));
    float g = smoothstep(0.3, 0.1, 1.) * 1.0 - smoothstep(0.54, 0.6, sqrt(uv.y));


    vec4 o4 = mix(o2, o3 + COLOR_3, 0.3 - (uv.x / 5.0));
    float tod = (sin(iTime / (3.14) * 0.5) + 0.5);
    float todnorm = mix(0.1, 0.4, tod);

    fragColor = mix(mix(o4, o4 * 1.-o3, 0.4 - (uv.y / 6.0)), (1.0 - g) * vec4(vec3(0.2), 1), 0.1) - vec4(todnorm);
}
