vec3 texSample(const int x, const int y, in vec2 fragCoord)
{
	vec2 uv = fragCoord.xy / iResolution.xy * iChannelResolution[0].xy;
	uv = (uv + vec2(x, y)) / iChannelResolution[0].xy;
	return texture(iChannel0, uv).xyz;       
}                                         
vec3 sharpenFilter(in vec2 fragCoord, float strength){
	vec3 f =
	texSample(-1,-1, fragCoord) *  -1. +                     
	texSample( 0,-1, fragCoord) *  -1. +                    
	texSample( 1,-1, fragCoord) *  -1. +                      
	texSample(-1, 0, fragCoord) *  -1. +                    
	texSample( 0, 0, fragCoord) *   9. +                     
	texSample( 1, 0, fragCoord) *  -1. +                      
	texSample(-1, 1, fragCoord) *  -1. +                     
	texSample( 0, 1, fragCoord) *  -1. +                     
	texSample( 1, 1, fragCoord) *  -1.
	;                                              
	return mix(texSample( 0, 0, fragCoord), f , strength);    
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){    
	vec2 uv = fragCoord/iResolution.xy;                    
	vec2 uvMouse = iMouse.xy/iResolution.xy;               
	if(uv.x > uvMouse.x){
		fragColor = vec4(sharpenFilter(fragCoord, uvMouse.y),1.0);             
	}else{
		fragColor = vec4(texSample( 0, 0, fragCoord),1.0);
    }
    if(uv.x > uvMouse.x - 0.002 && uv.x < uvMouse.x + 0.002){
    	fragColor = vec4(1.0,1.0,1.0,1.0);
	}
}
