float numColumns = 20.0;
float numRows = 20.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 newColor;

    float columns = mod(floor(uv.x * numColumns), 2.0); //columns
    float rows = mod(floor(uv.y * numRows), 2.0); //rows

    vec4 blankSquares = texture(iChannel0, uv);
    vec4 filledSquares = texture(iChannel1, uv);

    if( columns == 0.0 && rows == 0.0)
    {
    	newColor = blankSquares; //the "blank" squares
    }
    else if (columns != 0.0 && rows != 0.0)
    {
        newColor = blankSquares; //the "blank" squares
    }
    else
    {
        newColor = filledSquares; //the "filled" squares
    }

    fragColor = newColor;
}
