void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uvOrig = uv;

    uv.x = abs(-cos(iTime + uvOrig.y) * uvOrig.x);
    uv.y = abs(sin(iTime - uvOrig.x) * uvOrig.y);

    //uvOrig = uv;
    // Time varying pixel color
    vec3 col;
    col.x = 0.5*abs(cos(5.0*iTime+30.0*uv.y) / sin(13.0*iTime+30.0*uv.x));
    col.y = 0.5*abs(cos(7.0*iTime+30.0*uv.y+2.0) / sin(17.0*iTime+30.0*uv.x+2.0));
    col.z = 0.5*abs(cos(11.0*iTime+30.0*uv.y+4.0) / sin(3.0*iTime+30.0*uv.x+4.0));

	vec4 texCol=texture(iChannel0, uvOrig);
    // Output to screen
    //if(texCol.x>.5 && texCol.y<.1 &&texCol.z<1.1)
    if(texCol.x>0.2)
        fragColor = texCol;
    else
    	fragColor = vec4(col,1.0);
}
