//try 1
#define speed 1.0
#define Min_radius 0.25
#define Max_radius 0.50


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	float pi = 3.14;
	vec2 uv = fragCoord.xy;
	vec2 center = iMouse.xy;// iResolution.xy * 0.5;

    float var = abs(sin(speed*iTime)); 


	float radius = iResolution.y *(Min_radius
        + (Max_radius-Min_radius)*var );




 	vec4 backGround = vec4(1.0,0.0,1.0, 1.0);

    vec3 red = vec3(0.0, 0.0, 1.0);

    float d = length(center - uv) - radius;
	float t = clamp(d, 0.0, 1.0);
	vec4 layer2 =  vec4(red, (1.0 - t));

    float slope = abs(sin(d*.4)) ;
 	layer2.a *= slope; 

 	fragColor = mix(backGround, layer2, layer2.a);
}
