float edge = 0.1;
float dark = -0.3;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = fragCoord.xy / iResolution.xy; 
   vec2 dir = max(abs(uv * 2.0 - 1.0) * (1.0 + edge) - edge + dark, vec2(0));
   float v = dot(dir, dir);
   v = 1.0 - v / (1.0 + v);
   fragColor = vec4(v);
}
