#define APPLY 1

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	//uv.y *=-1.;


	 float filtre[25];
	for(int i = 0; i < 25; ++i)
	{
		filtre[i]=-1.;

	}
	filtre[12] = 24.;
	vec4 color = vec4(0.0);
	if(APPLY == 1){
		for (int i=0;i<5;i++)
		   for (int j=0;j<5;j++) {
			color += filtre[i*5 + j] * texture(iChannel0, uv + vec2(i-2,j-2)/100.);
		   }
		if((color.r+color.g+color.b)/3. < .8)
		{
			color = vec4(0.);
		}
	}
	else
			color= texture(iChannel0, uv);
	fragColor = color;
}
