float ball(vec2 uv, vec2 center, float radius)
{
    return length(uv-center) - radius;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float scale = 6.0;
	vec2 uv = scale*(fragCoord.xy*2.0-iResolution.xy) / iResolution.x;
    vec2 mouse = scale*(iMouse.xy*2.0-iResolution.xy) / iResolution.x;

    float tex = texture(iChannel0, uv-vec2(0.0,iTime)).r;

    float circles = ball(uv, vec2(0.0), 1.0);
    circles *= ball(uv, mouse, 0.5);
    circles *= ball(uv, vec2(cos(iTime)*1.5, sin(iTime)*0.5)*3.0, 0.5);

    circles = smoothstep(0.5, 0.7, abs(0.6 - circles)/tex);

	fragColor = vec4(circles);
}
