#define E (n=n.zxy, d(p+n)-d(p-n))
#define t normalize

float d(vec3 p)
{
    p.xy *= mat2(cos(p.z*=.1),-sin(p.z),sin(p.z),cos(p.z));
    return 9. - abs(p.y * .5);
}

void mainImage( out vec4 f, vec2 g )
{
    vec3 
        n = iResolution,
        r = t(vec3(g+g-n.xy,n.y)),
    	p=n-n; 	
    p.z = iTime * 15.;
    for (int i=80;i-->0;n.z=.1,p+=r*d(p)*.3);
	f = vec4(1,2,1,1) * d(p - t(vec3(E,E,E)));
}
