// Ed Markowski 2013
// Use the slider to adjust pixelation of texture.
bool inBox(vec2 lowLeft, vec2 topRight, vec2 fragCoord)
{
	return fragCoord.x >= lowLeft.x && fragCoord.x <= topRight.x
		&& fragCoord.y >= lowLeft.y && fragCoord.y <= topRight.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float offset0 = iResolution.y/25.0;

	float myMouseX = (iResolution.x - iMouse.x)/4.0;
	if (myMouseX > iResolution.x-offset0) myMouseX = iResolution.x-offset0;
	if (myMouseX < offset0) myMouseX = offset0;

	float texWidth = iChannelResolution[0].x;
	float block = (myMouseX/iResolution.x)*texWidth;
	if (0.0 == block) block = 1.0;

	if (texWidth <= block || iMouse.x < offset0)
		fragColor = texture(iChannel0, fragCoord.xy/iResolution.xy);
	else
	{
		vec2 tex;
		vec2 k = iChannelResolution[0].xy/block;
		vec2 rem = vec2(mod(fragCoord.x, floor(k.x)), mod(fragCoord.y, floor(k.y)));
		tex.s = (0.0 != rem.x) ? fragCoord.x + k.x - rem.x : fragCoord.x;
		tex.t = (0.0 != rem.y) ? fragCoord.y + k.y - rem.y : fragCoord.y;
		tex.st = (tex.st - 0.5*k.xy)/iResolution.xy;
		fragColor= texture(iChannel0, clamp(tex.st, vec2(0.0), vec2(1.0)));
	}

	float offset1 = offset0-iResolution.y/90.0;
	float scrollerHeight = iResolution.y/25.0;

	// layer 1
	vec2 lL = vec2(offset1, offset1);
	vec2 tR = vec2(iResolution.x-offset1, offset1+0.5*scrollerHeight);
	if (inBox(lL, tR, fragCoord)) fragColor = (fragCoord.x < iMouse.x) ? vec4(1.0) : vec4(0.5);
}
