#define MOUSE
//#define SS

const vec4 K = vec4(1., 2. / 3., 1. / 3., 3.);
vec3 hsv2rgb(vec3 c)
{
	// from http://lolengine.net/blog/2013/07/27/rgb-to-hsv-in-glsl
    vec3 p = abs(fract(c.xxx + K.xyz) * 6. - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0., 1.), c.y);
}

const float rads = 2. * 3.14159;
const float oscRate = 2.;

vec4 point(vec3 p)
{
    vec2 uv = p.xy;
    #ifdef MOUSE
    vec2 center = iMouse.xy / iResolution.x;
    float tMod = (center.x + 1.);
    float t = iTime * oscRate * tMod * tMod;

    #else
    float cx = sin(iTime) * 0.5 + 0.5;
    vec2 center = vec2(cx, sin(cx * rads) * 0.25 + 0.25);
    float t = iTime * oscRate;
    #endif

    float sinT = sin(t);

    float d = distance(center, uv);
    float rings = mod((d * d * 500.), (sinT + 1.5) * 10.) * 0.5 + 0.5;

    vec3 target = vec3(cos(t), sinT, 0.);
    float line = dot(target, vec3(uv - center, 1.));
    float ink = cos(sqrt(abs(line)) * 50.) * 0.5 + 0.5;

	return vec4(hsv2rgb(vec3(d,ink,rings)), 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // ss based on https://www.shadertoy.com/view/XslSW7
	vec3 p = vec3(fragCoord.xy / iResolution.x, 0.);    
	#ifdef SS
    float delta = 1.0 / iResolution.x;
    vec4 color = (point(p+delta*vec3(0.25,0.00,1.)) +
			      point(p+delta*vec3(0.75,0.25,1.)) +
                  point(p+delta*vec3(0.00,0.50,1.)) +
                  point(p+delta*vec3(0.50,0.75,1.))) / 4.0;
    #else
    vec4 color = point(p);
    #endif
    fragColor = color;
}
