uniform float iOvertoneVolume;

vec4 blue_lines(vec2 fragCoord) {
  float v = texture(iChannel0, vec2(0.5)).x;
  float t = iTime;

  vec2 uv = 2.0*(fragCoord.xy/iResolution.xy) - 1.0;

  float r, g, b = 0.0;
  float i, j, k = 0.0;

  float z = 6.0 + 4.0 * sin(t);

  i = sin(pow(uv.x*z,2.0)+ abs(sin(t*70.0)*pow(uv.y*z,2.0)));
  j = pow(i, 50.0*abs(sin(t*4.0*v)));
  b = j;

  return vec4(r, g, b, 1.0);
}

vec4 name_tbd(vec2 fragCoord) {
  float v = iOvertoneVolume;
  float t = iTime;

  vec2 uv = 2.0*(fragCoord.xy/iResolution.xy) - 1.0;

  float r, g, b = 0.0;
  float h, i, j, k = 0.0;

  float vv = 1.0;
  float z = 2.0;
  float w = 1000.0 + (1000.0 * pow(sin(t*3.0),3.0));

  h = (w * pow(uv.x*z,2.0)) + pow(uv.y*z,2.0);
  i = sin(h);
  if (h > 4.0) {
    i = 0.0;
  }
  j = pow(i, 50.0*abs(sin(vv*4.0)));
  b = j;

  return vec4(r, g, b, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  float t = iTime;

  fragColor = mix(blue_lines(fragCoord), name_tbd(fragCoord), sin(t));
}
