// Optimized port and generalization of 
// http://learningwebgl.com/blog/?p=2858
const float stride = 8.0; // Must be a multiple of 4
const bool wiggle = true;

void mainImage( out vec4 fragColor, in vec2 fragCoord )  {
  vec3 c = texture(iChannel0, fragCoord.xy / iResolution.xy).rgb;
  float v = dot(c, c);

  vec2 p = fragCoord.xy;
  if (wiggle) p.x += floor(sin(p.x * 0.04) * 2.0 + sin(p.y * 0.09 + p.x * 0.07));
  p = mod(p.xx + vec2(p.y, -p.y), vec2(stride));

  fragColor = vec4(vec3(
          float((v > 1.00) || (p.x != 0.0)) *
		  float((v > 0.70) || (p.y != 0.0)) *
  		  float((v > 0.35) || (p.x != stride / 2.0)) *
    	  float((v > 0.18) || (p.y != stride / 2.0)) *
          float((v > 0.10) || (p.x != stride / 4.0)) *
          float((v > 0.02) || (p.y != stride / 4.0))), 1.0);

}
