float IsWithinCircle(vec2 point, vec2 circleCenter, float circleRadius)
{
    circleCenter = vec2(circleCenter.x * iResolution.x, circleCenter.y * iResolution.y);
    circleRadius = circleRadius * iResolution.x;

    float len = length(point-circleCenter);

    return smoothstep( circleRadius*0.5, circleRadius*1.5, len);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float radius = 0.2;
    vec2 center_1 = vec2((sin(iTime*0.4) + 1.0)/2.2, (cos(iTime*0.5)+1.0)/2.2);
    vec2 center_2 = vec2((cos(iTime) + 1.0)/3.2, (sin(iTime)+1.0)/2.20);
    vec2 center_3 = vec2(0.5, (sin(iTime)+1.0)/2.2);
	vec2 center_4 = vec2( (cos(iTime*1.4)+1.0)/2.2, 0.3);

    float color = IsWithinCircle(fragCoord.xy, center_1, radius) + 
        IsWithinCircle(fragCoord.xy, center_2, radius) +
        IsWithinCircle(fragCoord.xy, center_3, radius) +
        IsWithinCircle(fragCoord.xy, center_4, radius);

    color = color /4.0;
    color = step(0.9, color);



    fragColor = vec4(color, color, color, 1.0);
	//vec2 uv = fragCoord.xy / iResolution.xy;
	//fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
}
