void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy / iResolution.xy;




    //shape position
    vec2 shapeCenter = vec2( 0.35, 0.67);
    //distance from pixel to shape center
    ////vec2 distanceToShape = p - shapeCenter;
    vec2 distanceToShape = p - shapeCenter;

    //color vector
    vec3 col = vec3( 0.9, 0.4, 0.0 );

    //shape radius
    float shapeRadius = 0.2 + 0.1*cos( atan(distanceToShape.x, distanceToShape.y)*10000.00
                           - 20.00*distanceToShape.x*iTime + 20.1*iTime  );

        //shape sharpness (smaller is sharper)
    float sharpnessMod = 0.008;

    col *= smoothstep( shapeRadius, shapeRadius+sharpnessMod, length(distanceToShape));
    //col = vec3( 1.0, 1.0, 1.0) - col;

    //tree trunk
    ////trunk width
    float trunkWidth = 0.022*iTime;
    //////trunk wiggle
    trunkWidth += 0.001*cos(70.0*distanceToShape.y);
    //////trunk expand downward
    trunkWidth += exp(-10.00*p.x);

    ////trunk smoothness mod
    float trunkSmoothMod = 0.002;

    //trunk draw
    col *= (1.00 - (1.00 - 
                    smoothstep( trunkWidth, trunkWidth+trunkSmoothMod, 
                               abs(distanceToShape.x - 0.2*sin(2.6*distanceToShape.y)) ))
                   *(1.00 - smoothstep( 0.0, 0.01, distanceToShape.y)));



    //finally changes the color
	fragColor = vec4(col,1.0);
}
