void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float aspect = iResolution.x / iResolution.y;

    float zscale = fract(iTime)* 1.5;
    //float zscale = 1.2;
	float focal = 1.5;


    float vx = (uv.x-0.5) * zscale * aspect;
    float vy = (uv.y-0.5) * zscale;

    float z = vx * vx + vy * vy + zscale ;

    uv.x = 1.0 - vx * ( focal / z ) +0.5;
    uv.y = 1.0 - vy * ( focal / z ) +0.5;

    if(uv.x < 2.0 && uv.x > 1.0 && uv.y < 2.0 && uv.y > 1.0) {
		fragColor = texture( iChannel0, uv );
    }else
    	fragColor = vec4(0.0,0.0,0.0,0.0);
}
