void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime;
	vec2 uv = 2.0  * (fragCoord.xy / iResolution.xy) - vec2(0.1,0.3);
    vec2 uv2 = 2.0  * (fragCoord.xy / iResolution.xy) - vec2(cos(time),sin(time));
    float vLength = length(uv);
    float vLength2 = length(dot(uv,uv2));
    vec4 color = vec4(cos(10.0*  vLength));
    float smoo = smoothstep(length(color),0.1,0.4);
    vec4 color2 = vec4(cos(10.0*  vLength2),0.0,0.0,0.0);
    vec4 mixColor = mix(color*smoo,color2,0.3);
   	fragColor = vec4(mixColor + vec4(uv2,0.0,0.0));
}
