#define POW (0.1)
float myPow( float x, float p )
{
 	if ( x > 0. )
        return  pow(  x, p );
    else
        return -pow( -x, p );
}
const float divs = 16.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime*0.2;
    float st = sin(t);
    float ct = cos(t);
	vec2 div = vec2( divs, divs*iResolution.y/iResolution.x );
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 xy0 = div*uv;
    vec2 xy;
    xy.x =  ct*xy0.x + st*xy0.y;
    xy.y = -st*xy0.x + ct*xy0.y;
    vec2 sxy = sin(3.14159*xy);
    vec2 pxy = vec2( myPow( sxy.x, POW ),
                     myPow( sxy.y, POW ) );
    float a = (pxy.x + pxy.y)*0.5;
    float c = a*0.5 + 0.5;
	fragColor = vec4(c, c, c, 1.0);
}
