float comb(vec2 pos) 
{
	pos.x=fract(pos.x+0.5)-.5;
	pos.y=fract(pos.y+0.5)-0.5;

	pos = abs(pos);
float fm = 1.0 + 0.75*sin(iTime);
	return (max(pos.x+pos.y*0.57735*fm,pos.y)-.5);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{
	float aspect = (iResolution.x / iResolution.y)*0.5,iChannel0;
	vec2 unipos = (( fragCoord.xy / iResolution.xy )+iChannel0);
	vec2 pos = unipos*10.0+iChannel0;
	pos.x *= aspect;
	float dist = comb(pos);
	float d = smoothstep( 0.03, 0.06, abs(dist));


	vec3 clr_a = vec3(0.9, 0.6, 0.2) * vec3(1.0 - abs(dist * 5.0));
	vec3 clr_b = vec3(0.3, 0.2, 0.0);
	float m = 0.5 + dist*6.0*(sin(iTime*1.125));
	vec3 clr_c = vec3( m, m*0.6, 0.15 );

	vec3 clr = mix(clr_a, clr_b, d);
	clr = mix(clr, clr_c, dist*-sin(d));
	fragColor.rgb = clr;
}

vec2 mainSound( in int samp,float time)
{
    return vec2( sin(6.2831*440.0*time)*exp(-3.0*time) );
}
