vec2 cmpxcjg(in vec2 c) {
	return vec2(c.x, -c.y);
}

vec2 cmpxmul(in vec2 a, in vec2 b) {
	return vec2(a.x * b.x - a.y * b.y, a.y * b.x + a.x * b.y);
}

vec2 cmpxdiv(in vec2 a, in vec2 b) {
    return cmpxmul(a, cmpxcjg(b));
}

float cmpxmag(in vec2 c) {
    return sqrt(c.x * c.x + c.y * c.y);
}

int julia(in vec2 z, in vec2 c, in int max) {
    int num = 0;
    while (cmpxmag(z) < 2.0 && num < max) {
    	z = cmpxmul(z, z) + c;
        num++;
    }
    return num;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float ratio = (iResolution.x / iResolution.y);
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 mouse = iMouse.xy / iResolution.xy - vec2(0.5, 0.5);
    mouse.x *= ratio;
    mouse *= -2.0;
    vec2 squareUv = (vec2(uv.x * ratio, uv.y) - vec2(1.0, 0.5)) * 3.0;

    // Coordinate in the fractal.
    vec2 inp = (uv - vec2(0.5, 0.5)) * 2.0;

   	// Constant in the fractal iteration.
    vec2 pos = vec2(cos(iTime), sin(iTime)) - mouse;

    // Calculate the number of steps until the input coordinate
    // tends towards infinity.
    int max = 30;
    int num = julia(inp, pos, max);
	float t = float(num) / float(max);
    fragColor = vec4(t, t, t, 1.0);

    // Render the mandelbrot below the coordinate system.
    num = julia(squareUv, squareUv, max);
    t = float(num) / float(max);
    fragColor = mix(fragColor, vec4(t, t, t, 1.0), t);

    // We want to display the current `pos` parameter as well.
    vec2 delta = abs(squareUv - pos);
    if (length(delta) < 0.03) {
        fragColor = vec4(1.0, 1.0, 0.5, 1.0);
    }
    // Also render the line on which the `pos` is rotating about.
    else if (abs(length(squareUv + mouse) - 1.0) <= 0.01) {
        fragColor = vec4(0.4, 0.3, 1.0, 1.0);
    }
}
