// derived from: https://www.shadertoy.com/view/MdBBWG
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float bass = texture(iChannel0,vec2(0,0)).r;
    float value = texture(iChannel0,vec2( 0.1*bass*fragCoord.x/iResolution.x,0)).r;
    float radius = 0.9 * min(iResolution.x*1., iResolution.y*1.) * sin(value);
    vec3 blur = 30.0 + 28.0 * sin(iTime * vec3(1, 1.1, 1.2)) * sin(value);
    vec2 c =( 0.5 + 0.01*sin(iTime)) * iResolution.xy;
    vec3 dist = smoothstep(-blur*5.0, blur*5.0, vec3(abs(distance(fragCoord, c) - radius)));
	fragColor = vec4(
        dist,
        1.0
    );
}
