void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	//vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 white = vec4(1.0, 1.0, 1.0, 1.0);
    vec4 black = vec4(0.0, 0.0, 0.0, 0.0);
    vec4 red = vec4(1.0, 0.0, 0.0, 1.0);
    vec4 magenta = vec4(1.0, 0.0, 1.0, 1.0);

    vec4 color = black;

    vec2 xy = fragCoord.xy;
    xy.x = xy.x / iResolution.x;
    xy.y = xy.y / iResolution.y;    

    vec4 textureColor = texture(iChannel0, xy);

    //remove red gradient
    //textureColor.r *= xy.y;

    //black and white
    //float normalizedWhite = (textureColor.r + textureColor.g + textureColor.b) / 3.0;
	//textureColor = vec4(normalizedWhite, normalizedWhite, normalizedWhite, 1.0);

    //textureColor.r *= abs(sin(iTime));
    //textureColor.g *= abs(cos(iTime));
    //textureColor.b *= abs(sin(iTime) * cos(iTime));

    float darkLimit = 0.5;
    if(textureColor.r + textureColor.b < textureColor.g) {
        textureColor = texture(iChannel1, xy);      
    }
    else if(textureColor.r < textureColor.g
              && textureColor.b < textureColor.g
              && textureColor.g > 0.4) {
        vec4 c = texture(iChannel1, xy);
        float greenp = textureColor.g / (textureColor.r + textureColor.b);
        float notgreen = 1.0 - greenp;
        textureColor.r = (c.r * greenp) + (textureColor.r * notgreen);
        textureColor.g = (c.g * greenp) + (textureColor.g * notgreen);
        textureColor.b = (c.b * greenp) + (textureColor.b * notgreen);

        //textureColor.g = (c.g + textureColor.g) / 2.0;
        //textureColor.g = c.g;
        //textureColor.b = (c.b + textureColor.b) / 2.0;
        //textureColor = magenta;
    }
    else if(textureColor.r < darkLimit && textureColor.g < darkLimit && textureColor.b < darkLimit) {
        textureColor = texture(iChannel2, xy);
    }

	fragColor = textureColor;
}
