void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float angle = iTime;
    vec2 uv2 = vec2(
        (uv.x - .5) * cos(angle) - (uv.y - .5) * sin(angle) + .5,
    	(uv.x - .5) * sin(angle) + (uv.y - .5) * cos(angle) + .5
    );
    if (uv2.x < .0 || uv2.x > 1.0 || uv2.y < .0 || uv2.y > 1.0) {
        fragColor = vec4(1);
    } else {
    	fragColor = texture(iChannel0, uv2);
    }
}
