void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float t = iTime;
    float s = sin(iTime);
    float r = texture(iChannel1, vec2(t*0.001,t*0.002), 2.0).x;
    vec4 rr = texture(iChannel1, vec2(t*0.01,t*0.02));

    fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
    fragColor = mix(fragColor, fragColor * mod(uv.y+t*0.1+s*abs(s)*0.1, 0.1), 0.5);
    fragColor *= 2.1 - uv.x*uv.x*0.01*sin(iTime*2.0*r);

    float c = 1.0 - pow(length(uv - vec2(0.5)), 1.0);
    fragColor *= c;
    fragColor.y += r * c * 0.1;

    fragColor = mix(fragColor, vec4(fragColor.x,1,0,1) * mod(uv.x, 0.1), 0.1);
    fragColor = mix(fragColor, texture(iChannel0, c*fragColor.xy*4.0), 0.1); 

    fragColor.x *= fragColor.z*5.0;
    fragColor.xy += rr.xy;

    vec4 mm = texture(iChannel2, vec2(uv.x*uv.y*c*c*c,0));

    //fragColor = mm;
    fragColor.x *= mm.x;
}
