void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float radius = 64.0;
    float top = iResolution.y - radius;
    float right = iResolution.x - radius;
    vec4 fill = vec4(0.0, 1.0, 0.0, 1.0);
    vec4 none = vec4(1.0, 1.0, 1.0, 1.0);

    if (fragCoord.x < radius) {
      if (fragCoord.y < radius) {
        if (distance(fragCoord, vec2(radius, radius)) <= radius) {
          fragColor = fill;
        } else {
          fragColor = none;
        }
      } else if (fragCoord.y > top) {
        if (distance(fragCoord, vec2(radius, top)) <= radius) {
          fragColor = fill;
        } else {
          fragColor = none;
        }
      } else {
        fragColor = fill;
      }
    } else if (fragCoord.x > right) {
      if (fragCoord.y < radius) {
        if (distance(fragCoord, vec2(right, radius)) <= radius) {
          fragColor = fill;
        } else {
          fragColor = none;
        }
      } else if (fragCoord.y > top) {
        if (distance(fragCoord, vec2(right, top)) <= radius) {
          fragColor = fill;
        } else {
          fragColor = none;
        }
      } else {
        fragColor = fill;
      }
    } else {
      fragColor = fill;
    }
}
