// Line Segment "Waves" written 2015 by JT

void mainImage( out vec4 o, in vec2 I )
{
    I = 2. * I / iResolution.xy - 1.;
    o -= o;
    const float n = 20.;
    for(float i = 0.; i < n; i++)
    {
        vec4 Q = 2. * abs(2. * fract((iTime - i * .2) * vec4(.0432, .0123, .0257, .0332)) - 1.) - 1.;
        Q.wz -= Q.xy;
        o += cos(length(I - (Q.xy + Q.wz * clamp(dot(I - Q.xy, Q.wz) / dot(Q.wz, Q.wz), 0., 1.)))*50.);
    }
}
